import React, { useState, useEffect } from 'react';
import { Box, Heading, VStack, Text, Input, Button, HStack, FormControl, FormLabel, NumberInput, NumberInputField, NumberInputStepper, NumberIncrementStepper, NumberDecrementStepper, Alert, AlertIcon, Slider, SliderTrack, SliderFilledTrack, SliderThumb, Progress, Textarea, Modal, ModalOverlay, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton, Table, Thead, Tbody, Tr, Th, Td } from '@chakra-ui/react';

interface BudgetItem {
  category: string;
  amount: number;
}

interface SavingsGoal {
  name: string;
  targetAmount: number;
  currentAmount: number;
  targetDate: string;
  notes: string;
}

const defaultCategories = ['Rent/Mortgage', 'Electricity', 'Water', 'Phone', 'Internet', 'Food'];

const FamilyView: React.FC = () => {
  const [budgetItems, setBudgetItems] = useState<BudgetItem[]>(
    defaultCategories.map(category => ({ category, amount: 0 }))
  );
  const [newCategory, setNewCategory] = useState('');
  const [newAmount, setNewAmount] = useState('');
  const [isNewGoalModalOpen, setIsNewGoalModalOpen] = useState(false);

  // Loan state
  const [loanAmount, setLoanAmount] = useState<number>(0);
  const [interestRate, setInterestRate] = useState<number>(0);
  const [loanTerm, setLoanTerm] = useState<number>(0);
  const [monthlyPayment, setMonthlyPayment] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);

  // Savings state
  const [currentSavings, setCurrentSavings] = useState<number>(0);
  const [savingsGoal, setSavingsGoal] = useState<number>(0);
  const [monthlySavingsContribution, setMonthlySavingsContribution] = useState<number>(0);
  const [longTermSavingsGoals, setLongTermSavingsGoals] = useState<SavingsGoal[]>([]);
  const [newGoalName, setNewGoalName] = useState('');
  const [newGoalAmount, setNewGoalAmount] = useState<number>(0);
  const [newGoalDate, setNewGoalDate] = useState('');
  const [newGoalNotes, setNewGoalNotes] = useState('');
  const [savingsGrowthRate, setSavingsGrowthRate] = useState<number>(0.05); // 5% annual growth rate
  const [savingsProjectionYears, setSavingsProjectionYears] = useState<number>(10);

  // Retirement state
  const [currentRetirementSavings, setCurrentRetirementSavings] = useState<number>(0);
  const [retirementGoal, setRetirementGoal] = useState<number>(0);
  const [monthlyRetirementContribution, setMonthlyRetirementContribution] = useState<number>(0);
  const [retirementGrowthRate, setRetirementGrowthRate] = useState<number>(0.07); // 7% annual growth rate
  const [retirementProjectionYears, setRetirementProjectionYears] = useState<number>(30);

  // TODO: Implement fetching of children's accounts data from the backend

  const calculateCompoundInterest = (principal: number, monthlyContribution: number, annualRate: number, years: number) => {
    const monthlyRate = annualRate / 12;
    const months = years * 12;
    let total = principal;

    for (let i = 0; i < months; i++) {
      total += monthlyContribution;
      total *= (1 + monthlyRate);
    }

    return total;
  };

  const createProjectionData = (initial: number, monthly: number, rate: number, years: number) => {
    const data = [];
    for (let year = 0; year <= years; year++) {
      const projectedAmount = calculateCompoundInterest(initial, monthly, rate, year);
      data.push({
        year,
        amount: Math.round(projectedAmount),
      });
    }
    return data;
  };

  const savingsProjectionData = createProjectionData(currentSavings, monthlySavingsContribution, savingsGrowthRate, savingsProjectionYears);
  const retirementProjectionData = createProjectionData(currentRetirementSavings, monthlyRetirementContribution, retirementGrowthRate, retirementProjectionYears);

  useEffect(() => {
    // Add savings and retirement contributions to budget
    const updatedBudgetItems = [...budgetItems];
    const savingsIndex = updatedBudgetItems.findIndex(item => item.category === 'Savings');
    const retirementIndex = updatedBudgetItems.findIndex(item => item.category === 'Retirement');

    if (savingsIndex === -1) {
      updatedBudgetItems.push({ category: 'Savings', amount: monthlySavingsContribution });
    } else {
      updatedBudgetItems[savingsIndex].amount = monthlySavingsContribution;
    }

    if (retirementIndex === -1) {
      updatedBudgetItems.push({ category: 'Retirement', amount: monthlyRetirementContribution });
    } else {
      updatedBudgetItems[retirementIndex].amount = monthlyRetirementContribution;
    }

    setBudgetItems(updatedBudgetItems);
  }, [monthlySavingsContribution, monthlyRetirementContribution]);

  const addBudgetItem = () => {
    if (newCategory && newAmount) {
      setBudgetItems([...budgetItems, { category: newCategory, amount: parseFloat(newAmount) }]);
      setNewCategory('');
      setNewAmount('');
    }
  };

  const removeBudgetItem = (index: number) => {
    setBudgetItems(budgetItems.filter((_, i) => i !== index));
  };

  const calculateLoanPayment = () => {
    if (loanAmount <= 0 || interestRate <= 0 || loanTerm <= 0) {
      setError("Please enter valid values for all fields.");
      return;
    }

    const monthlyRate = interestRate / 100 / 12;
    const numberOfPayments = loanTerm * 12;
    const payment = (loanAmount * monthlyRate * Math.pow(1 + monthlyRate, numberOfPayments)) / (Math.pow(1 + monthlyRate, numberOfPayments) - 1);
    setMonthlyPayment(payment);
    setError(null);
  };

  const clearLoanCalculator = () => {
    setLoanAmount(0);
    setInterestRate(0);
    setLoanTerm(0);
    setMonthlyPayment(null);
    setError(null);
  };

  const calculateYearsToRetirementGoal = () => {
    if (retirementGoal <= currentRetirementSavings || monthlyRetirementContribution <= 0) {
      return 0;
    }
    const remainingAmount = retirementGoal - currentRetirementSavings;
    const yearsToGoal = remainingAmount / (monthlyRetirementContribution * 12);
    return Math.ceil(yearsToGoal);
  };

  // Removed duplicate declaration of isNewGoalModalOpen

  const openNewGoalModal = () => setIsNewGoalModalOpen(true);
  const closeNewGoalModal = () => setIsNewGoalModalOpen(false);

  const addNewGoal = () => {
    const newGoal: SavingsGoal = {
      name: newGoalName,
      targetAmount: newGoalAmount,
      currentAmount: 0,
      targetDate: newGoalDate,
      notes: newGoalNotes
    };
    setLongTermSavingsGoals([...longTermSavingsGoals, newGoal]);
    closeNewGoalModal();
    setNewGoalName('');
    setNewGoalAmount(0);
    setNewGoalDate('');
    setNewGoalNotes('');
  };

  return (
    <Box>
      <Heading as="h2" size="xl" mb={4}>Family Account Overview</Heading>
      <VStack spacing={4} align="stretch">
        <Text>Child 1 Account (Placeholder)</Text>
        <Text>Child 2 Account (Placeholder)</Text>
        {/* TODO: Map through children's accounts and display them */}

        <Heading as="h3" size="lg" mt={6}>Budget</Heading>
        {budgetItems.map((item, index) => (
          <HStack key={index} justifyContent="space-between">
            <Text>{item.category}</Text>
            <Text>${item.amount.toFixed(2)}</Text>
            <Button size="sm" onClick={() => removeBudgetItem(index)}>Remove</Button>
          </HStack>
        ))}

        <HStack as="form" onSubmit={(e) => { e.preventDefault(); addBudgetItem(); }}>
          <FormControl>
            <FormLabel>Category</FormLabel>
            <Input
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              placeholder="New category"
            />
          </FormControl>
          <FormControl>
            <FormLabel>Amount</FormLabel>
            <Input
              type="number"
              value={newAmount}
              onChange={(e) => setNewAmount(e.target.value)}
              placeholder="Amount"
            />
          </FormControl>
          <Button type="submit" mt={8}>Add Item</Button>
        </HStack>

        <Heading as="h3" size="lg" mt={6}>Savings</Heading>
        <FormControl>
          <FormLabel>Current Savings</FormLabel>
          <NumberInput min={0} value={currentSavings} onChange={(valueString) => setCurrentSavings(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <FormControl>
          <FormLabel>Monthly Savings Contribution</FormLabel>
          <NumberInput min={0} value={monthlySavingsContribution} onChange={(valueString) => setMonthlySavingsContribution(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <Text>Total Savings: ${(currentSavings + monthlySavingsContribution * 12).toFixed(2)}</Text>

        <Heading as="h4" size="md" mt={4}>Long-Term Savings Goals</Heading>
        {longTermSavingsGoals.map((goal, index) => (
          <Box key={index} borderWidth="1px" borderRadius="lg" p={4} mt={2}>
            <Text fontWeight="bold">{goal.name}</Text>
            <Text>Target: ${goal.targetAmount.toFixed(2)}</Text>
            <Text>Current: ${goal.currentAmount.toFixed(2)}</Text>
            <Text>Target Date: {new Date(goal.targetDate).toLocaleDateString()}</Text>
            <Text>Notes: {goal.notes}</Text>
            <Progress value={(goal.currentAmount / goal.targetAmount) * 100} colorScheme="blue" mt={2} />
          </Box>
        ))}

        <Button mt={4} onClick={openNewGoalModal}>Add New Goal</Button>

        <Modal isOpen={isNewGoalModalOpen} onClose={closeNewGoalModal}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Add New Long-Term Savings Goal</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <FormControl>
                <FormLabel>Goal Name</FormLabel>
                <Input value={newGoalName} onChange={(e) => setNewGoalName(e.target.value)} />
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>Target Amount</FormLabel>
                <NumberInput min={0} value={newGoalAmount} onChange={(valueString) => setNewGoalAmount(Number(valueString))}>
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper />
                    <NumberDecrementStepper />
                  </NumberInputStepper>
                </NumberInput>
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>Target Date</FormLabel>
                <Input type="date" value={newGoalDate} onChange={(e) => setNewGoalDate(e.target.value)} />
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>Notes</FormLabel>
                <Textarea value={newGoalNotes} onChange={(e) => setNewGoalNotes(e.target.value)} />
              </FormControl>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme="blue" mr={3} onClick={addNewGoal}>
                Add Goal
              </Button>
              <Button variant="ghost" onClick={closeNewGoalModal}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        <Heading as="h3" size="lg" mt={6}>Retirement</Heading>
        <FormControl>
          <FormLabel>Current Retirement Savings</FormLabel>
          <NumberInput min={0} value={currentRetirementSavings} onChange={(valueString) => setCurrentRetirementSavings(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <FormControl>
          <FormLabel>Retirement Goal</FormLabel>
          <NumberInput min={0} value={retirementGoal} onChange={(valueString) => setRetirementGoal(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <FormControl>
          <FormLabel>Monthly Retirement Contribution</FormLabel>
          <NumberInput min={0} value={monthlyRetirementContribution} onChange={(valueString) => setMonthlyRetirementContribution(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <Text>Years to Reach Goal: {calculateYearsToRetirementGoal()}</Text>
        <Progress value={(currentRetirementSavings / retirementGoal) * 100} colorScheme="green" />

        <Heading as="h3" size="lg" mt={6}>Loan Calculator</Heading>
        <FormControl>
          <FormLabel>Loan Amount</FormLabel>
          <NumberInput min={0} value={loanAmount} onChange={(valueString) => setLoanAmount(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <FormControl>
          <FormLabel>Interest Rate (%)</FormLabel>
          <NumberInput min={0} max={100} step={0.1} value={interestRate} onChange={(valueString) => setInterestRate(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <FormControl>
          <FormLabel>Loan Term (years)</FormLabel>
          <NumberInput min={1} max={30} value={loanTerm} onChange={(valueString) => setLoanTerm(Number(valueString))}>
            <NumberInputField />
            <NumberInputStepper>
              <NumberIncrementStepper />
              <NumberDecrementStepper />
            </NumberInputStepper>
          </NumberInput>
        </FormControl>
        <HStack>
          <Button onClick={calculateLoanPayment}>Calculate</Button>
          <Button onClick={clearLoanCalculator}>Clear</Button>
        </HStack>
        {error && (
          <Alert status="error">
            <AlertIcon />
            {error}
          </Alert>
        )}
        {monthlyPayment !== null && !error && (
          <Alert status="success">
            <AlertIcon />
            Monthly Payment: ${monthlyPayment.toFixed(2)}
          </Alert>
        )}
      </VStack>
    </Box>
  );
};

export default FamilyView;
