import React, { useState } from 'react';
import { Box, Button, FormControl, FormLabel, Input, VStack, Heading, RadioGroup, Radio, Stack } from '@chakra-ui/react';

interface LoginSignupProps {
  onLogin: (type: 'parent' | 'child') => void;
}

const LoginSignup: React.FC<LoginSignupProps> = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState<'parent' | 'child'>('parent');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you would typically make an API call to authenticate the user
    // For now, we'll just simulate a successful login
    onLogin(userType);
  };

  return (
    <Box width="100%" maxWidth="400px">
      <VStack spacing={4} as="form" onSubmit={handleSubmit}>
        <Heading size="lg">Login / Sign Up</Heading>
        <FormControl>
          <FormLabel>Username</FormLabel>
          <Input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </FormControl>
        <FormControl>
          <FormLabel>Password</FormLabel>
          <Input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </FormControl>
        <FormControl as="fieldset">
          <FormLabel as="legend">Account Type</FormLabel>
          <RadioGroup onChange={(value) => setUserType(value as 'parent' | 'child')} value={userType}>
            <Stack direction="row">
              <Radio value="parent">Parent</Radio>
              <Radio value="child">Child</Radio>
            </Stack>
          </RadioGroup>
        </FormControl>
        <Button type="submit" colorScheme="blue" width="full">
          Login / Sign Up
        </Button>
      </VStack>
    </Box>
  );
};

export default LoginSignup;
