import React, { useState, useEffect } from 'react';
import { ChakraProvider, Box } from '@chakra-ui/react';
import { motion } from 'framer-motion';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LandingPage from './components/LandingPage';
import LoginSignup from './components/LoginSignup';
import FamilyView from './components/FamilyView';
import ChildView from './components/ChildView';

const MotionBox = motion(Box);

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userType, setUserType] = useState<'parent' | 'child' | null>(null);

  useEffect(() => {
    console.log('My Little Bank App component mounted');
    document.title = 'My Little Bank';
  }, []);

  console.log('Rendering My Little Bank App component', { isLoggedIn, userType });

  return (
    <ChakraProvider>
      <Router>
        <MotionBox
          minHeight="100vh"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <Routes>
            <Route path="/" element={<LandingPage />} />
            <Route path="/login" element={
              <LoginSignup onLogin={(type) => {
                console.log('Login attempted', { type });
                setIsLoggedIn(true);
                setUserType(type);
              }} />
            } />
            <Route
              path="/dashboard"
              element={
                isLoggedIn
                  ? (userType === 'parent' ? <FamilyView /> : <ChildView />)
                  : <Navigate to="/login" replace />
              }
            />
          </Routes>
        </MotionBox>
      </Router>
    </ChakraProvider>
  );
}

export default App;
