import React from 'react';
import { Box, VStack, Heading, Text, Button, Container, useColorModeValue, Flex, Spacer } from '@chakra-ui/react';
import { useNavigate } from 'react-router-dom';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();
  const bgColor = useColorModeValue('gray.50', 'gray.800');
  const textColor = useColorModeValue('gray.600', 'gray.200');

  const handleLoginSignup = () => {
    navigate('/login');
  };

  return (
    <Box bg={bgColor} minH="100vh">
      <Flex as="nav" align="center" justify="space-between" wrap="wrap" padding="1.5rem" bg="blue.500" color="white">
        <Flex align="center" mr={5}>
          <Heading as="h1" size="lg" letterSpacing={'-.1rem'}>
            My Little Bank
          </Heading>
        </Flex>
        <Spacer />
        <Box>
          <Button
            onClick={handleLoginSignup}
            colorScheme="whiteAlpha"
          >
            Login / Sign Up
          </Button>
        </Box>
      </Flex>
      <Container maxW="container.xl" py={16}>
        <VStack spacing={8} align="center" textAlign="center">
          <Heading as="h2" size="2xl" mb={4}>
            Welcome to My Little Bank
          </Heading>
          <Text fontSize="xl" color={textColor} maxW="2xl">
            Learn financial literacy through a fun and interactive banking simulation designed for children and families.
          </Text>
          <VStack spacing={4} align="stretch" width="full" maxW="md">
            <Heading as="h3" size="lg">
              Key Features:
            </Heading>
            <Text>• Virtual money management</Text>
            <Text>• Loan simulation with variable terms</Text>
            <Text>• Customizable budgeting tools</Text>
            <Text>• Educational financial literacy content</Text>
            <Text>• Savings and goal planning</Text>
            <Text>• Simulated allowance system</Text>
          </VStack>
        </VStack>
      </Container>
    </Box>
  );
};

export default LandingPage;
